def add(i, y):
    """
    显示两个值相加
    :param i: 第一个
    :param y: 第二个
    :return:
    """
    print(str(i) + " + " + str(y) + " = " + str((i + y)))


def sub(i, y):
    print(str(i) + " - " + str(y) + " = " + str((i - y)))


def mul(i, y):
    print(str(i) + " * " + str(y) + " = " + str((i * y)))


def div(i, y):
    print(str(i) + " / " + str(y) + " = " + str((i / y)))
